import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { deleteTask, toggleComplete } from "../features/tasks/taskSlice";

const TaskList = () => {
  const tasks = useSelector((state) => state.tasks);
  const dispatch = useDispatch();
  const [filter, setFilter] = useState("ALL");

  // Get today's date in YYYY-MM-DD format
  const today = new Date().toISOString().split("T")[0];

  // Filter tasks based on selected tab
  const filteredTasks = tasks.filter((task) => {
    if (filter === "ALL") return true;
    if (filter === "TODAY") return task.date === today;
    if (filter === "COMPLETED") return task.completed === true;
    return true;
  });

  return (
    <div className="max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Tasks</h2>

      {/* Filter Tabs */}
      <div className="flex gap-3 mb-6">
        <button
          onClick={() => setFilter("ALL")}
          className={`px-4 py-2 rounded ${
            filter === "ALL" ? "bg-blue-500 text-white" : "bg-gray-200"
          }`}
        >
          All
        </button>
        <button
          onClick={() => setFilter("TODAY")}
          className={`px-4 py-2 rounded ${
            filter === "TODAY" ? "bg-blue-500 text-white" : "bg-gray-200"
          }`}
        >
          Today
        </button>
        <button
          onClick={() => setFilter("COMPLETED")}
          className={`px-4 py-2 rounded ${
            filter === "COMPLETED" ? "bg-blue-500 text-white" : "bg-gray-200"
          }`}
        >
          Completed
        </button>
      </div>

      {/* Task List */}
      {filteredTasks.length === 0 ? (
        <p className="text-gray-500">No tasks available.</p>
      ) : (
        filteredTasks.map((task) => (
          <div
            key={task.id}
            className="bg-white shadow-md rounded-lg p-4 mb-4 border"
          >
            <div className="flex justify-between items-center">
              <div>
                <h3
                  className={`text-xl font-semibold ${
                    task.completed ? "line-through text-gray-400" : ""
                  }`}
                >
                  {task.title}
                </h3>
                <p className="text-gray-700">{task.description}</p>
                <p className="text-gray-500">Due: {task.date}</p>
              </div>

              <div className="flex items-center gap-3">
                {/* Mark Complete */}
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => dispatch(toggleComplete(task.id))}
                />
                {/* Delete Button */}
                <button
                  onClick={() => dispatch(deleteTask(task.id))}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </div>
            </div>

            {/* Subtasks */}
            {task.subTasks.length > 0 && (
              <div className="mt-3">
                <h4 className="font-bold">Subtasks:</h4>
                <ul className="list-disc pl-5">
                  {task.subTasks.map((sub, i) => (
                    <li key={i}>
                      <p
                        className={`font-semibold ${
                          task.completed ? "line-through text-gray-400" : ""
                        }`}
                      >
                        {sub.title}
                      </p>
                      <p className="text-gray-600">{sub.description}</p>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default TaskList;
