import React, { useState } from "react";

const AddTask = ({ addTask }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim() || !date) return;
    addTask({ title, description, date });
    setTitle("");
    setDescription("");
    setDate("");
  };

  return (
    <div className="bg-[#0f172a] shadow-xl rounded-2xl p-6 max-w-lg mx-auto mt-8 border border-gray-700">
      <h2 className="text-2xl font-bold text-white mb-5 text-center">
        Add New Task
      </h2>

      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        {/* Title Input */}
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="px-4 py-3 bg-[#1e293b] text-white rounded-lg outline-none border border-gray-600 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-500 transition"
        />

        {/* Description Input */}
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="px-4 py-3 bg-[#1e293b] text-white rounded-lg outline-none border border-gray-600 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-500 transition"
          rows="3"
        />

        {/* Date Input */}
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="px-4 py-3 bg-[#1e293b] text-white rounded-lg outline-none border border-gray-600 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-500 transition"
        />

        {/* Buttons */}
        <div className="flex items-center gap-3">
          <button
            type="button"
            className="bg-green-500 hover:bg-green-600 text-white font-semibold px-4 py-2 rounded-lg transition"
          >
            + Add Sub-task
          </button>
          <button
            type="submit"
            className="bg-indigo-500 hover:bg-indigo-600 text-white font-semibold px-5 py-2 rounded-lg transition"
          >
            Add Task
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddTask;
