import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addTask } from "../features/tasks/taskSlice";
import SubTaskInput from "./SubtaskInput";
import { v4 as uuidv4 } from "uuid";

const TaskForm = () => {
  const dispatch = useDispatch();
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [subTasks, setSubTasks] = useState([]);

  const handleAddSubTask = () => {
    setSubTasks([...subTasks, { title: "", description: "" }]);
  };

  const handleSubTaskChange = (index, e) => {
    const newSubTasks = [...subTasks];
    newSubTasks[index][e.target.name] = e.target.value;
    setSubTasks(newSubTasks);
  };

  const handleRemoveSubTask = (index) => {
    setSubTasks(subTasks.filter((_, i) => i !== index));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim() === "" || date.trim() === "") return;

    const newTask = {
      id: uuidv4(),
      title,
      description,
      date,
      subTasks: subTasks.filter((s) => s.title.trim() !== ""),
    };

    dispatch(addTask(newTask));

    setTitle("");
    setDescription("");
    setDate("");
    setSubTasks([]);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-xl mx-auto bg-white shadow-md rounded-lg p-6 mt-6"
    >
      <h2 className="text-2xl font-bold mb-4">Add New Task</h2>

      {/* Title */}
      <input
        type="text"
        placeholder="Task Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="border w-full p-2 mb-3 rounded"
      />

      {/* Description */}
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="border w-full p-2 mb-3 rounded"
      />

      {/* Date */}
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="border w-full p-2 mb-3 rounded"
        min={new Date().toISOString().split("T")[0]}
      />

      {/* Subtasks */}
      {subTasks.map((subTask, index) => (
        <SubTaskInput
          key={index}
          index={index}
          subTask={subTask}
          handleSubTaskChange={handleSubTaskChange}
          handleRemoveSubTask={handleRemoveSubTask}
        />
      ))}

      {/* Add Subtask Button */}
      <button
        type="button"
        onClick={handleAddSubTask}
        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 mb-3"
      >
        + Add Sub-task
      </button>

      {/* Submit */}
      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Add Task
      </button>
    </form>
  );
};

export default TaskForm;
